// Betfair account data
var betfair = require("../index.js");
var async = require('async');
var common = require('./common.js');

var root = '../../../'
, env = process.env.NODE_ENV || 'development'
, config = require(root + 'config/config')[env]


// Create session to Betfair
var settings = common.settings;
settings.session = betfair.newSession();
settings.login = process.env['BF_LOGIN'] || config.betfair.user;
settings.password = process.env['BF_PASSWORD'] || config.betfair.password;

console.log(config.betfair.user);

async.series([common.login, common.logout], function (err, res) {
    console.log("Done");
    process.exit(0);
});